import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
  constructor() {
    super();
    this.state = {
      article: [],
      loading: false
    }
  }
  async componentDidMount() {
    fetch("https://newsapi.org/v2/everything?domains=wsj.com&apiKey=ae0f9319f30c480c9c64938595575355").then((res) => res.json())
     .then((json) => {
        this.setState({
          article: json.articles,
          loading: false
        });
      })

    // console.log(this.state.article);
  }
  render() {
    return (
      <div className='yo'>
        {this.state.article.map((element) => {
          return <NewsItem key={element.url} title={element.title} description={element.description} urli={element.urlToImage} url={element.url} />
        })}
        <div>

        <button type="button" class="btn btn-warning">Previous</button>
        <button type="button" class="btn btn-warning">Next</button>
        </div>
      </div>
    )
  }
}

export default News
