import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';

export class News extends Component {
  constructor() {
    super();
    this.state = {
      article: [],
      loading: false,
      page:1
    }
  }

  async componentDidMount() {
    fetch(`https://newsapi.org/v2/top-headlines?country=in&apiKey=ae0f9319f30c480c9c64938595575355&page=${this.state.page}`).then((res) => res.json())
      .then((json) => {
        this.setState({
          article: json.articles,
          loading: false,
        });
      })

    // console.log(this.state.article);
  }
  prev = () => {
    console.log("Prev");
    this.setState({
      page: this.state.page - 1,
      loading:true
    })
    this.componentDidMount()
  }
  next = () => {
    if (this.state.page<4) {

      console.log("Next");
      this.setState({
        page: this.state.page + 1,
       loading:true
      })
      this.componentDidMount()
    }
  }
  render() {
    return (
      <div>
        <div className='yo'>
          {!this.state.loading && this.state.article.map((element) => {
            return <NewsItem key={element.url} title={element.title} description={element.description} urli={element.urlToImage} url={element.url} />
          })}
        </div>
        <div>
          {this.state.loading && <Spinner/>}
        </div>
        <div className="d-flex justify-content-around">
          <button disabled={this.state.page <= 1} type="button" onClick={this.prev} className="btn btn-warning">&larr; Previous</button>
          <button type="button" disabled={this.state.page >= 6}  className="btn btn-warning" onClick={this.next}>Next &rarr;</button>
        </div>
      </div>
    )
  }
}

export default News
