import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
  constructor(){
    super();
    this.state={
      article:[],
      loading:false
    }
  }
  async componentDidMount() {
    let data= await fetch("https://newsapi.org/v2/everything?q=tesla&from=2022-02-08&sortBy=publishedAt&apiKey=ae0f9319f30c480c9c64938595575355");
    let pdata= await data.json();
    this.setState({article:pdata.articles})


  }
  render() {
    return (
      <div className='yo'>
        {this.state.article.map((element)=>{
        return <NewsItem key={element.url} title={element.title} description={element.description} urli={element.urlToImage} url={element.url}/>
          })}
      </div>
    )
  }
}

export default News
