import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
  constructor() {
    super();
    this.state = {
      article: [],
      loading: false
    }
  }
  async componentDidMount() {
    fetch("https://newsapi.org/v2/everything?domains=wsj.com&apiKey=ae0f9319f30c480c9c64938595575355&page=1").then((res) => res.json())
     .then((json) => {
        this.setState({
          article: json.articles,
          loading: false,
          page:1
        });
      })

    // console.log(this.state.article);
  }
  prev=()=>{
    console.log("Prev");
  }
  next=()=>{
    console.log("Next");

  }
  render() {
    return (
      <div>
      <div className='yo'>
        {this.state.article.map((element) => {
          return <NewsItem key={element.url} title={element.title} description={element.description} urli={element.urlToImage} url={element.url} />
        })}
        </div>
        <div class="d-flex justify-content-around">
        <button disabled={this.state.page<=1} type="button" onClick={this.prev} class="btn btn-warning">&larr; Previous</button>
        <button type="button" class="btn btn-warning" onClick={this.next}>Next &rarr;</button>
        </div>
      </div>
    )
  }
}

export default News
